function initObsIntegration(){const warning=document.getElementById('warning');const streamOverlay=document.getElementById('stream_overlay');const overlayTextWrapper=document.getElementById('overlay_text_wrapper');const overlayText=document.getElementById('overlay_text');const overlayCam=document.getElementById('overlay_cam');const particles=document.getElementById('particles-js');const TRANSITION_ACTIONS={Starting_Soon:()=>{overlayText.textContent='STARTING SOON'},Be_Right_Back:()=>{overlayText.textContent='BE RIGHT BACK'},Stream_Ending:()=>{overlayText.textContent='STREAM ENDING'},Show_BG:()=>{particles.classList.add('show_background')},Hide_BG:()=>{particles.classList.remove('show_background')},Show_Particles:()=>{particles.style.opacity="1"},Hide_Particles:()=>{particles.style.opacity="0"},Show_Cam:()=>{overlayTextWrapper.classList.add('text_position_top');overlayCam.classList.add('show_cam')},Hide_Cam:()=>{overlayTextWrapper.classList.remove('text_position_top');overlayCam.classList.remove('show_cam')},Show_Text:()=>{overlayText.style.width="100%"},Hide_Text:()=>{overlayText.style.width="0"},Show_Text_Opacity:()=>{overlayTextWrapper.style.opacity="1"},Hide_Text_Opacity:()=>{overlayTextWrapper.style.opacity="0"},};function triggerBlurIn(el){el.classList.remove("blur_in","blur_out");void el.offsetWidth;el.classList.add("blur_in")}
function triggerBlurOut(el){el.classList.remove("blur_in","blur_out");void el.offsetWidth;el.classList.add("blur_out")}
if(!window.obsstudio){console.warn('obsstudio API not available (probably not running inside OBS)');warning.style.display='block';document.body.style.backgroundColor="#000";const devPanel=document.createElement('div');devPanel.style.position='fixed';devPanel.style.top='10px';devPanel.style.left='10px';devPanel.style.zIndex='9999';devPanel.style.background='rgba(0,0,0,0.7)';devPanel.style.color='#fff';devPanel.style.padding='8px';devPanel.style.fontSize='12px';devPanel.innerHTML=`
            <button data-t="Starting_Soon">Starting_Soon</button>
            <button data-t="Be_Right_Back">Be_Right_Back</button>
            <button data-t="Stream_Ending">Stream_Ending</button>
            <button data-t="Show_BG">Show_BG</button>
            <button data-t="Hide_BG">Hide_BG</button>
            <button data-t="Show_Particles">Show_Particles</button>
            <button data-t="Hide_Particles">Hide_Particles</button>
            <button data-t="Show_Cam">Show_Cam</button>
            <button data-t="Hide_Cam">Hide_Cam</button>
            <button data-t="Show_Text">Show_Text</button>
            <button data-t="Hide_Text">Hide_Text</button>
            <button data-t="Show_Text_Opacity">Show_Text_Opacity</button>
            <button data-t="Hide_Text_Opacity">Hide_Text_Opacity</button>
        `;document.body.appendChild(devPanel);devPanel.addEventListener('click',(e)=>{const t=e.target.dataset.t;if(t&&TRANSITION_ACTIONS[t]){TRANSITION_ACTIONS[t]()}});TRANSITION_ACTIONS.Show_Particles()
TRANSITION_ACTIONS.Show_Text_Opacity()
return}else{console.log(window.obsstudio.pluginVersion)}
window.addEventListener('obsTransitionChanged',function(event){const transition_name=event.detail.name
console.log('Transition:',transition_name);const action=TRANSITION_ACTIONS[transition_name];if(!action){console.warn('Unknown transition:',transition_name);return}
action()})}
document.addEventListener('DOMContentLoaded',initObsIntegration)